details.content:
.loading-spinner {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100px;
}
.spinner {
  border: 4px solid #f3f3f3;
  border-top: 4px solid #3498db;
  border-radius: 50%;
  width: 50px;
  height: 50px;
  animation: spin 1s linear infinite;
}
@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
.optionsBtn {
  display: inline-block;
  width: 35%;
  background: #000;
  border-radius: 150px;
  padding: 10px;
  margin-right: 10px;
  color: #fff;
  border: none;
  cursor: pointer;
}
.optionsBtn:hover {
  background: #333;
}
.optionsBtn:focus {
  outline: none;
}
.capture-img, .capture-video {
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
}
